## Lesson Structure

### Main Entry (index.ts)

```typescript
// packages/onboard/src/lessons/auth/index.ts

import { Lesson, LessonStep } from '../../types';
import { checkAllServices, AUTH_CHECKS } from '../../lib/authChecks';
import { tailscaleLesson } from './tailscale';
import { claudeLesson } from './claude';
import { codexLesson } from './codex';
import { geminiLesson } from './gemini';

const serviceLessons = [
  tailscaleLesson,
  claudeLesson,
  codexLesson,
  geminiLesson,
];

export const authLesson: Lesson = {
  id: 'auth',
  title: 'Authenticate Services',
  description: 'Connect your coding tools and services',

  async run(ui) {
    // Check current auth status
    const statuses = checkAllServices();

    // Show overview
    ui.clear();
    ui.header('Service Authentication');
    ui.text('Let\'s connect your services. We\'ll go through each one.');
    ui.blank();

    for (const lesson of serviceLessons) {
      const status = statuses[lesson.serviceId];
      const icon = status.authenticated ? '✓' : '○';
      const detail = status.authenticated 
        ? status.details || 'Authenticated'
        : 'Not configured';
      ui.text(`  ${icon} ${lesson.name.padEnd(15)} - ${detail}`);
    }

    ui.blank();
    await ui.prompt('Press Enter to start...');

    // Run each lesson
    for (const lesson of serviceLessons) {
      const status = statuses[lesson.serviceId];

      if (status.authenticated) {
        // Already authenticated, ask if want to re-auth
        ui.clear();
        ui.header(lesson.name);
        ui.success(`Already authenticated${status.details ? `: ${status.details}` : ''}!`);

        const choice = await ui.prompt('[Enter to continue, r to re-authenticate]');
        if (choice.toLowerCase() !== 'r') {
          continue;
        }
      }

      await lesson.run(ui);

      // Re-check status after lesson
      const newStatus = AUTH_CHECKS[lesson.serviceId]();
      statuses[lesson.serviceId] = newStatus;
    }

    // Summary
    ui.clear();
    ui.header('Authentication Complete!');
    ui.blank();

    const authenticated = Object.values(statuses).filter(s => s.authenticated).length;
    const total = Object.keys(statuses).length;

    ui.text(`${authenticated} of ${total} services authenticated.`);
    ui.blank();

    for (const lesson of serviceLessons) {
      const status = statuses[lesson.serviceId];
      const icon = status.authenticated ? '✓' : '✗';
      ui.text(`  ${icon} ${lesson.name}`);
    }

    ui.blank();
    await ui.prompt('Press Enter to return to menu...');
  },
};
```

### Individual Service Lessons

```typescript
// packages/onboard/src/lessons/auth/tailscale.ts

import { ServiceLesson } from '../../types';
import { checkTailscale } from '../../lib/authChecks';

export const tailscaleLesson: ServiceLesson = {
  serviceId: 'tailscale',
  name: 'Tailscale',

  async run(ui) {
    ui.clear();
    ui.header('Tailscale Setup');
    ui.blank();

    ui.text('Tailscale gives you secure remote access to your VPS.');
    ui.text('Once connected, you can SSH from anywhere!');
    ui.blank();

    ui.text('Run this command:');
    ui.blank();
    ui.code('sudo tailscale up');
    ui.blank();

    ui.text('This will open a browser (or show a URL).');
    ui.text('Log in with your Google account.');
    ui.blank();

    const choice = await ui.prompt('[Press Enter when done, or s to skip]');

    if (choice.toLowerCase() === 's') {
      ui.warn('Skipped. You can set up Tailscale later.');
      await ui.sleep(1500);
      return;
    }

    // Verify authentication
    ui.text('Checking Tailscale status...');
    await ui.sleep(1000);

    const status = checkTailscale();

    if (status.authenticated) {
      ui.blank();
      ui.success('Tailscale connected!');
      ui.text(`Your Tailscale IP: ${status.details}`);
      ui.blank();
      ui.text('Now you can SSH to this machine from any device');
      ui.text('with Tailscale installed.');
      ui.blank();

      await ui.prompt('[Press Enter to continue]');
    } else {
      ui.blank();
      ui.warn('Tailscale not yet connected.');
      ui.text('You can try again or set it up later.');

      const retry = await ui.prompt('[r to retry, Enter to continue]');
      if (retry.toLowerCase() === 'r') {
        return this.run(ui);  // Recursive retry
      }
    }
  },
};
